DELIMITER $$

/*!50003 DROP TRIGGER*//*!50032 IF EXISTS */ /*!50003 `tgReservation_AI` */$$

/*!50003 CREATE */ /*!50017 DEFINER = 'root'@'localhost' */ /*!50003 TRIGGER `tgReservation_AI` AFTER INSERT ON `ctrlt_reservation` FOR EACH ROW BEGIN
    DECLARE occDay date;
    DECLARE quantity int;
    /*SET occDay = NEW.dArrival;*/
    SET quantity = 1;
    WHILE quantity <= NEW.RESVC_QUANTITY DO
      SET occDay = NEW.RESVC_ARRIVAL;
      REPEAT
        INSERT INTO occupation VALUES
	      (NEW.RESVP_ID_HOTEL,       NEW.RESVF_ID_ROOMTYPE,      occDay,
	       NEW.RESVP_ID_RESERVATION, NEW.RESVC_RESERVATION_TYPE, NEW.RESVC_QUANTITY,
	       NEW.RESVF_ID_ROOM,        NEW.RESVC_ADULTS,           NEW.RESVC_CHILDREN, NEW.RESVC_STATUS);
        SET occDay = adddate(occDay, INTERVAL 1 DAY);
      UNTIL occDay >= NEW.RESVC_DEPARTURE
      END REPEAT;
      SET quantity = quantity + 1;
    END WHILE;
END */$$


DELIMITER ;

/* Trigger structure for table `ctrlt_reservation` */

DELIMITER $$

/*!50003 DROP TRIGGER*//*!50032 IF EXISTS */ /*!50003 `tgReservation_AU` */$$

/*!50003 CREATE */ /*!50017 DEFINER = 'root'@'localhost' */ /*!50003 TRIGGER `tgReservation_AU` AFTER UPDATE ON `ctrlt_reservation` FOR EACH ROW BEGIN
##  DECLARE nights tinyint(3) unsigned;
  DECLARE occDay date;
  DECLARE quantity int;
/* If any of arrival, departure or quantity vary,
 then delete all records in 'occupation' table
*/
  IF NEW.RESVC_ARRIVAL   = OLD.RESVC_ARRIVAL   AND
     NEW.RESVC_DEPARTURE = OLD.RESVC_DEPARTURE AND
     NEW.RESVC_QUANTITY  = OLD.RESVC_QUANTITY THEN
     UPDATE occupation
        SET tRoomType          = NEW.RESVF_ID_ROOMTYPE,
            tRoomNumber        = NEW.RESVF_ID_ROOM,
            nAdults            = NEW.RESVC_ADULTS,
            nChildren          = NEW.RESVC_CHILDREN,
            tStatus            = NEW.RESVC_STATUS
      WHERE nIdHotel = NEW.RESVP_ID_HOTEL AND nReservation = NEW.RESVP_ID_RESERVATION;
  ELSE
     DELETE FROM occupation
            WHERE nIdHotel       = NEW.RESVP_ID_HOTEL AND
                  nReservation = NEW.RESVP_ID_RESERVATION;
## Arrival MUST BE less than Departure !!
     SET quantity = 1;
     WHILE quantity <= NEW.RESVC_QUANTITY DO
       SET occDay = NEW.RESVC_ARRIVAL;
       REPEAT
         INSERT INTO occupation
           VALUES (NEW.RESVP_ID_HOTEL, NEW.RESVF_ID_ROOMTYPE, occDay, NEW.RESVP_ID_RESERVATION, NEW.RESVF_ID_ROOM,
                   NEW.RESVC_ADULTS,  NEW.RESVC_CHILDREN, NEW.RESVC_STATUS);
       SET occDay = adddate(occDay, INTERVAL 1 DAY);
       UNTIL occDay >= NEW.RESVC_DEPARTURE
       END REPEAT;
       SET quantity = quantity + 1;
     END WHILE;
  END IF;
END */$$


DELIMITER ;

